// TaskList.js
import React, { useEffect, useState } from "react";
import { getTasks } from "../api"; // Assuming you have a function to fetch tasks
import { FaEdit, FaTrashAlt } from "react-icons/fa"; // Edit and Delete icons
// import EditTaskModal from "./EditTaskModal"; // Import the new EditTaskModal component
import "./TaskList.css";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [isModalOpen, setModalOpen] = useState(false);
  const [currentTask, setCurrentTask] = useState(null); // Store the current task for editing

  // Fetch tasks from the backend
  const fetchTasks = async () => {
    try {
      const taskList = await getTasks(); // Replace with actual API call
      setTasks(taskList);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  useEffect(() => {
    fetchTasks(); // Load tasks on page load
  }, []);

  // Handle task update after editing
  const handleTaskUpdated = (updatedTask) => {
    setTasks((prevTasks) =>
      prevTasks.map((task) => (task.id === updatedTask.id ? updatedTask : task))
    );
  };

  // Handle Edit task
  const handleEditTask = (task) => {
    setCurrentTask(task); // Set the task to be edited
    setModalOpen(true); // Open the modal
  };

  // Handle Delete task
  const handleDeleteTask = (taskId) => {
    // Implement delete functionality
    console.log(`Delete task with ID: ${taskId}`);
  };

  return (
    <div className="task-list-container">
      <h1>Real-Time Task Management Dashboard</h1>
      <button onClick={() => setModalOpen(true)} className="add-task-btn">Add Task</button>

      <table className="task-table">
        <thead>
          <tr>
            <th>Task ID</th>
            <th>Task Name</th>
            <th>Status</th>
            <th>Created At</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <tr key={task.id}>
              <td>{task.id}</td>
              <td>{task.name}</td>
              <td>{task.status}</td>
              <td>{task.createdAt}</td>
              <td>
                <FaEdit
                  className="icon edit-icon"
                  onClick={() => handleEditTask(task)} // Open modal with selected task
                />
                <FaTrashAlt
                  className="icon delete-icon"
                  onClick={() => handleDeleteTask(task.id)} // Handle delete
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Edit Task Modal */}
   
    </div>
  );
};

export default TaskList;
