import React, { useState, useEffect } from "react";
import { getTasks, deleteTask, updateTaskStatus } from "../api"; // Import functions from api.js
import { FaEdit, FaTrash } from 'react-icons/fa'; // Use icons for Edit and Delete

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [isModalOpen, setModalOpen] = useState(false);

  // Fetch tasks from the backend
  const fetchTasks = async () => {
    try {
      const taskList = await getTasks(); // Fetch tasks from the API
      setTasks(taskList);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  useEffect(() => {
    fetchTasks(); // Load tasks on page load
  }, []);

  // Handle Delete action
  const handleDelete = async (taskId) => {
    try {
      await deleteTask(taskId); // Delete task from the backend
      setTasks(tasks.filter((task) => task.id !== taskId)); // Update the UI by removing the task
    } catch (error) {
      console.error("Error deleting task:", error);
    }
  };

  // Handle Edit action
  const handleEdit = (taskId) => {
    // Here, you can open the modal for updating task (you can pass taskId to pre-fill form in the modal)
    console.log("Edit task with ID:", taskId);
    // Set up modal logic for editing, maybe pass taskId to TaskModal
  };

  return (
    <div>
      <h1>Task List</h1>

      <table>
        <thead>
          <tr>
            <th>Task ID</th>
            <th>Task Name</th>
            <th>Status</th>
            <th>Created At</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <tr key={task.id}>
              <td>{task.id}</td>
              <td>{task.name}</td>
              <td>{task.status}</td>
              <td>{task.createdAt}</td>
              <td className="action-icons">
                <FaEdit
                  onClick={() => handleEdit(task.id)} // Open Edit modal
                  className="edit-icon"
                />
                <FaTrash
                  onClick={() => handleDelete(task.id)} // Delete task
                  className="delete-icon"
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TaskList;
