import React, { useState, useEffect } from "react";
import { updateTaskStatus } from "../api"; // API to update task status

const EditTaskForm = ({ task, onClose, onTaskUpdated }) => {
  const [name, setName] = useState(task.name);
  const [status, setStatus] = useState(task.status);

  useEffect(() => {
    setName(task.name);
    setStatus(task.status);
  }, [task]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const updatedTask = await updateTaskStatus(task.id, { name, status });
      onTaskUpdated(updatedTask); // Update the task in the parent component
      onClose(); // Close the modal
    } catch (error) {
      console.error("Error updating task:", error);
    }
  };

  return (
    <div className="edit-task-form-container">
      <h2>Edit Task</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Task Name</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Status</label>
          <select
            value={status}
            onChange={(e) => setStatus(e.target.value)}
            required
          >
            <option value="Pending">Pending</option>
            <option value="In Progress">In Progress</option>
            <option value="Completed">Completed</option>
          </select>
        </div>
        <button type="submit">Update Task</button>
        <button type="button" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
};

export default EditTaskForm;
