import React, { useEffect, useState } from "react";
import { getTasks } from "../api"; // Assuming you have a function to fetch tasks
import TaskModal from "./TaskModal";
import "./TaskList.css"; 
import ActionIcons from "./ActionIcons";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [isModalOpen, setModalOpen] = useState(false);
  const [currentTask, setCurrentTask] = useState(null);

  // Fetch tasks from the backend
  const fetchTasks = async () => {
    try {
      const taskList = await getTasks(); // Replace with actual API call
      setTasks(taskList);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  useEffect(() => {
    fetchTasks(); // Load tasks on page load
  }, []);

 // Show the Edit Task modal
 const handleEditTask = (task) => {
  setCurrentTask(task); // Set the task data to be edited
  setModalOpen(true);
};

  // Show the Add Task modal
  const handleAddTask = () => {
    setModalOpen(true);
  };

  // Handle task addition
  const handleTaskAdded = (newTask) => {
    setTasks((prevTasks) => [...prevTasks, newTask]); // Add new task to the list
  };

  return (
    <div className="task-list-container">
      <h1>Real-Time Task Management Dashboard</h1>
      <button onClick={handleAddTask} className="add-task-btn">Add Task</button>

      <table className="task-table">
        <thead>
          <tr>
            <th>Task ID</th>
            <th>Task Name</th>
            <th>Status</th>
            <th>Created At</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <tr key={task.id}>
              <td>{task.id}</td>
              <td>{task.name}</td>
              <td>{task.status}</td>
              <td>{task.createdAt}</td>
              <td>{task.action}</td>
              <td>
                <ActionIcons onEditClick={() => handleEditTask(task)} /> {/* Edit Icon */}
              </td>
            </tr>
            
          ))}
        </tbody>
      </table>

      {/* Add Task Modal */}
      <TaskModal
        isOpen={isModalOpen}
        onClose={() => setModalOpen(false)}
        onTaskAdded={handleTaskAdded}
      />
    </div>
  );
};

export default TaskList;
