import React, { useEffect, useState } from "react";
import { getTasks, updateTaskStatus } from "../api"; // Assuming you have an API utility
import TaskModal from "./TaskModal";
import EditTaskModal from "./EditTaskModal"; // Import the EditTaskModal component
import "./TaskList.css";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [isModalOpen, setModalOpen] = useState(false);
  const [isEditModalOpen, setEditModalOpen] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null);

  // Fetch tasks from the backend
  const fetchTasks = async () => {
    try {
      const taskList = await getTasks();
      setTasks(taskList);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  // Show the Add Task modal
  const handleAddTask = () => {
    setModalOpen(true);
  };

  // Handle task addition
  const handleTaskAdded = (newTask) => {
    setTasks((prevTasks) => [...prevTasks, newTask]);
  };

  // Handle Edit icon click
  const handleEditClick = (task) => {
    setSelectedTask(task); // Set the selected task for editing
    setEditModalOpen(true);
  };

  // Handle task update
  const handleTaskUpdated = async (updatedTask) => {
    try {
      await updateTaskStatus(updatedTask.id, updatedTask); // Update the task on the backend
      setTasks((prevTasks) =>
        prevTasks.map((task) =>
          task.id === updatedTask.id ? updatedTask : task
        )
      );
      setEditModalOpen(false); // Close the edit modal
    } catch (error) {
      console.error("Error updating task:", error);
    }
  };

  return (
    <div className="task-list-container">
      <h1>Real-Time Task Management Dashboard</h1>
      <button onClick={handleAddTask} className="add-task-btn">
        Add Task
      </button>

      <table className="task-table">
        <thead>
          <tr>
            <th>Task ID</th>
            <th>Task Name</th>
            <th>Status</th>
            <th>Created At</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <tr key={task.id}>
              <td>{task.id}</td>
              <td>{task.name}</td>
              <td>{task.status}</td>
              <td>{task.createdAt}</td>
              <td>
                <span
                  className="edit-icon"
                  onClick={() => handleEditClick(task)}
                >
                  ✏️
                </span>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Add Task Modal */}
      <TaskModal
        isOpen={isModalOpen}
        onClose={() => setModalOpen(false)}
        onTaskAdded={handleTaskAdded}
      />

      {/* Edit Task Modal */}
      <EditTaskModal
        isOpen={isEditModalOpen}
        onClose={() => setEditModalOpen(false)}
        task={selectedTask}
        onTaskUpdated={handleTaskUpdated}
      />
    </div>
  );
};

export default TaskList;
