import React, { useState } from "react";
import { addTask } from "../api"; // Replace with your API call
import './TaskModal.css';
const TaskModal = ({ isOpen, onClose, onTaskAdded }) => {
  const [taskData, setTaskData] = useState({ name: "", status: "Pending" });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  // Handle input changes for Task Name and Status
  const handleChange = (e) => {
    setTaskData({
      ...taskData,
      [e.target.name]: e.target.value,
    });
  };

  // Handle form submission to add the task
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate the form
    if (!taskData.name.trim()) {
      setMessage("Task name cannot be empty.");
      return;
    }

    setLoading(true);
    setMessage(""); // Reset message

    try {
      const result = await addTask(taskData); // Add task through API
      setMessage("Task added successfully!");
      onTaskAdded(result); // Notify parent component to refresh task list
      setTaskData({ name: "", status: "Pending" }); // Reset form
    } catch (error) {
      setMessage("Error saving task: " + error.message);
    } finally {
      setLoading(false); // Stop loading state
    }
  };

  // If the modal is not open, return null (don't render anything)
  if (!isOpen) return null;

  return (
    <div className="modal">
      <div className="modal-content">
        <h2>Add Task</h2>
        {message && <div className="message">{message}</div>}

        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Enter Task Name"
            value={taskData.name}
            onChange={handleChange}
          />
          <select name="status" value={taskData.status} onChange={handleChange}>
            <option value="Pending">Pending</option>
            <option value="In Progress">In Progress</option>
            <option value="Completed">Completed</option>
          </select>
          <div>
          <button type="submit" disabled={loading} className="submit-task-btn">
            {loading ? "Save..." : "Save"}
          </button>
          </div>
        </form>
        <button onClick={onClose} className="close-modal-btn">
          Cancel
        </button>
      </div>
    </div>
  );
};

export default TaskModal;
