// TaskList.js
import React, { useEffect, useState } from "react";
import { getTasks, updateTask } from "../api"; // Assuming API methods for get and update
import TaskModal from "./TaskModal";
import ActionIcons from "./ActionIcons"; // Import ActionIcons
import "./TaskList.css"; // Import CSS

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [isModalOpen, setModalOpen] = useState(false);
  const [currentTask, setCurrentTask] = useState(null); // Store the task being edited

  // Fetch tasks from the backend
  const fetchTasks = async () => {
    try {
      const taskList = await getTasks();
      setTasks(taskList);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  // Show the Add Task modal
  const handleAddTask = () => {
    setCurrentTask(null); // Clear current task for adding new one
    setModalOpen(true);
  };

  // Show the Edit Task modal
  const handleEditTask = (task) => {
    setCurrentTask(task); // Set the task data to be edited
    setModalOpen(true);
  };

  // Handle task addition or update
  const handleTaskUpdated = (updatedTask) => {
    setTasks((prevTasks) =>
      prevTasks.map((task) =>
        task.id === updatedTask.id ? updatedTask : task
      )
    );
  };

  return (
    <div className="task-list-container">
      <h1>Real-Time Task Management Dashboard</h1>
      <button onClick={handleAddTask} className="add-task-btn">
        Add Task
      </button>

      <table className="task-table">
        <thead>
          <tr>
            <th>Task ID</th>
            <th>Task Name</th>
            <th>Status</th>
            <th>Created At</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <tr key={task.id}>
              <td>{task.id}</td>
              <td>{task.name}</td>
              <td>{task.status}</td>
              <td>{task.createdAt}</td>
              <td>
                <ActionIcons onEditClick={() => handleEditTask(task)} /> {/* Edit Icon */}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Add or Edit Task Modal */}
      <TaskModal
        isOpen={isModalOpen}
        onClose={() => setModalOpen(false)}
        onTaskUpdated={handleTaskUpdated}
        task={currentTask} // Pass current task for editing
      />
    </div>
  );
};

export default TaskList;
