// TaskList.js
import React, { useEffect, useState } from "react";
import { getTasks } from "../api"; // Assuming you have a function to fetch tasks
import ActionIcons from "../assets/edit.svg"; // The icons for edit
import UpdateTaskStatusModal from "./UpdateTaskStatusModal"; // Import the modal component
import "./TaskList.css"; // Import CSS

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [isModalOpen, setModalOpen] = useState(false);
  const [currentTask, setCurrentTask] = useState(null); // Store the task being edited

  // Fetch tasks from the backend
  const fetchTasks = async () => {
    try {
      const taskList = await getTasks(); // Replace with actual API call
      setTasks(taskList);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  // Show the Edit Task Status modal
  const handleEditTask = (task) => {
    setCurrentTask(task); // Set the task data to be edited
    setModalOpen(true);
  };

  // Handle task update after editing
  const handleTaskUpdated = (updatedTask) => {
    setTasks((prevTasks) =>
      prevTasks.map((task) =>
        task.id === updatedTask.id ? updatedTask : task
      )
    );
  };

  return (
    <div className="task-list-container">
      <h1>Real-Time Task Management Dashboard</h1>

      <table className="task-table">
        <thead>
          <tr>
            <th>Task ID</th>
            <th>Task Name</th>
            <th>Status</th>
            <th>Created At</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <tr key={task.id}>
              <td>{task.id}</td>
              <td>{task.name}</td>
              <td>{task.status}</td>
              <td>{task.createdAt}</td>
              <td>
                <ActionIcons onEditClick={() => handleEditTask(task)} />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Edit Task Status Modal */}
      <UpdateTaskStatusModal
        isOpen={isModalOpen}
        onClose={() => setModalOpen(false)}
        task={currentTask}
        onTaskUpdated={handleTaskUpdated}
      />
    </div>
  );
};

export default TaskList;
