import React, { useEffect, useState } from "react";
import { getTasks, deleteTask } from "../api"; // Include deleteTask function
import { FaEdit, FaTrash } from "react-icons/fa"; // Import Edit and Delete icons
import TaskModal from "./TaskModal";
import "./TaskList.css";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [isModalOpen, setModalOpen] = useState(false);
  const [taskToEdit, setTaskToEdit] = useState(null); // Store task to be edited

  // Fetch tasks from the backend
  const fetchTasks = async () => {
    try {
      const taskList = await getTasks(); // API call to fetch tasks
      setTasks(taskList);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  useEffect(() => {
    fetchTasks(); // Load tasks on page load
  }, []);

  // Show the Add Task modal
  const handleAddTask = () => {
    setTaskToEdit(null); // Clear any task in edit mode
    setModalOpen(true);
  };

  // Handle task addition
  const handleTaskAdded = (newTask) => {
    setTasks((prevTasks) => [...prevTasks, newTask]); // Add new task to the list
  };

  // Handle Edit action
  const handleEdit = (task) => {
    setTaskToEdit(task); // Set the task to be edited
    setModalOpen(true); // Open the modal for editing
  };

  // Handle Delete action
  const handleDelete = async (taskId) => {
    try {
      await deleteTask(taskId); // Call the API to delete the task
      setTasks(tasks.filter((task) => task.id !== taskId)); // Remove the task from the UI
    } catch (error) {
      console.error("Error deleting task:", error);
    }
  };

  return (
    <div className="task-list-container">
      <h1>Real-Time Task Management Dashboard</h1>
      <button onClick={handleAddTask} className="add-task-btn">Add Task</button>

      <table className="task-table">
        <thead>
          <tr>
            <th>Task ID</th>
            <th>Task Name</th>
            <th>Status</th>
            <th>Created At</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <tr key={task.id}>
              <td>{task.id}</td>
              <td>{task.name}</td>
              <td>{task.status}</td>
              <td>{new Date(task.createdAt).toLocaleString()}</td>
              <td>
                <FaEdit
                  onClick={() => handleEdit(task)} // Trigger edit action
                  className="edit-icon"
                />
                <FaTrash
                  onClick={() => handleDelete(task.id)} // Trigger delete action
                  className="delete-icon"
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Add/Edit Task Modal */}
      <TaskModal
        isOpen={isModalOpen}
        onClose={() => setModalOpen(false)}
        onTaskAdded={handleTaskAdded}
        taskToEdit={taskToEdit} // Pass the task data to modal for editing
      />
    </div>
  );
};

export default TaskList;
