import React, { useEffect, useState } from "react";
import { getTasks, deleteTask, updateTaskStatus } from "../api"; // Assuming you have these functions in api.js
import { FaEdit, FaTrash } from "react-icons/fa"; // Using react-icons for Edit and Delete icons
import TaskModal from "./TaskModal";
import "./TaskList.css"; // Ensure the CSS is imported for styling

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [isModalOpen, setModalOpen] = useState(false);
  const [taskToEdit, setTaskToEdit] = useState(null);

  // Fetch tasks from the backend
  const fetchTasks = async () => {
    try {
      const taskList = await getTasks(); // Replace with actual API call
      setTasks(taskList);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  useEffect(() => {
    fetchTasks(); // Load tasks on page load
  }, []);

  // Show the Add Task modal
  const handleAddTask = () => {
    setTaskToEdit(null); // Clear any task that might be in edit mode
    setModalOpen(true);
  };

  // Handle task addition
  const handleTaskAdded = (newTask) => {
    setTasks((prevTasks) => [...prevTasks, newTask]); // Add new task to the list
  };

  // Handle Edit action
  const handleEdit = (task) => {
    setTaskToEdit(task); // Set the task that we want to edit
    setModalOpen(true); // Open the modal to edit the task
  };

  // Handle Delete action
  const handleDelete = async (taskId) => {
    try {
      await deleteTask(taskId); // Send delete request to API
      setTasks(tasks.filter((task) => task.id !== taskId)); // Remove deleted task from UI
    } catch (error) {
      console.error("Error deleting task:", error);
    }
  };

  return (
    <div className="task-list-container">
      <h1>Real-Time Task Management Dashboard</h1>
      <button onClick={handleAddTask} className="add-task-btn">Add Task</button>

      <table className="task-table">
        <thead>
          <tr>
            <th>Task ID</th>
            <th>Task Name</th>
            <th>Status</th>
            <th>Created At</th>
            <th>Action</th>
          </tr>
        </thead>
        {/* <tbody>
          {tasks.map((task) => (
            <tr key={task.id}>
              <td>{task.id}</td>
              <td>{task.name}</td>
              <td>{task.status}</td>
              <td>{task.createdAt}</td>
              <td>
                <FaEdit
                  onClick={() => handleEdit(task)} // Open Edit modal
                  className="edit-icon"
                />
                <FaTrash
                  onClick={() => handleDelete(task.id)} // Delete the task
                  className="delete-icon"
                />
              </td>
            </tr>
          ))}
        </tbody> */}


        <tbody>
  {tasks.length > 0 ? (
    tasks.map((task) => (
      <tr key={task.id}>
        <td>{task.id}</td>
        <td>{task.name}</td>
        <td>{task.status}</td>
        <td>{task.createdAt}</td>
        <td>
          <FaEdit onClick={() => handleEdit(task)} className="edit-icon" />
          <FaTrash onClick={() => handleDelete(task.id)} className="delete-icon" />
        </td>
      </tr>
    ))
  ) : (
    <tr>
      <td colSpan="5" style={{ textAlign: "center" }}>
        No tasks available
      </td>
    </tr>
  )}
</tbody>

      </table>

      {/* Add/Edit Task Modal */}
      <TaskModal
        isOpen={isModalOpen}
        onClose={() => setModalOpen(false)}
        onTaskAdded={handleTaskAdded}
        taskToEdit={taskToEdit} // Pass the task data to the modal if we are editing
      />
    </div>
  );
};

export default TaskList;
