// UpdateTaskStatusModal.js
import React, { useState, useEffect } from "react";
import { updateTaskStatus } from "../api"; // Assuming updateTaskStatus API method

const UpdateTaskStatusModal = ({ isOpen, onClose, task, onTaskUpdated }) => {
  const [status, setStatus] = useState("Pending");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  // Set task data for editing
  useEffect(() => {
    if (task) {
      setStatus(task.status);
    }
  }, [task]);

  const handleStatusChange = (e) => {
    setStatus(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    setLoading(true);
    setMessage("");

    try {
      const updatedTask = await updateTaskStatus(task.id, { status });
      onTaskUpdated(updatedTask);
      setMessage("Task status updated successfully!");
    } catch (error) {
      setMessage("Error updating status: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal">
      <div className="modal-content">
        <h2>Edit Task Status</h2>
        {message && <div>{message}</div>}

        <form onSubmit={handleSubmit}>
          <select value={status} onChange={handleStatusChange}>
            <option value="Pending">Pending</option>
            <option value="In Progress">In Progress</option>
            <option value="Completed">Completed</option>
          </select>
          <button type="submit" disabled={loading}>
            {loading ? "Submitting..." : "Update Status"}
          </button>
        </form>
        <button onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

export default UpdateTaskStatusModal;
