// TaskList.js
import React, { useEffect, useState } from "react";
import { getTasks, deleteTask } from "../api"; // Import deleteTask function
import ActionIcons from "./ActionIcons"; // Import ActionIcons
import UpdateTaskStatusModal from "./UpdateTaskStatusModal"; // Import the modal component
import "./TaskList.css"; // Import CSS

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [isModalOpen, setModalOpen] = useState(false);
  const [currentTask, setCurrentTask] = useState(null); // Store the task being edited

  // Fetch tasks from the backend
  const fetchTasks = async () => {
    try {
      const taskList = await getTasks(); // Replace with actual API call
      setTasks(taskList);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []); // Run once when component mounts

  // Show the Edit Task Status modal
  const handleEditTask = (task) => {
    setCurrentTask(task); // Set the task data to be edited
    setModalOpen(true);
  };

  // Handle task update after editing
  const handleTaskUpdated = (updatedTask) => {
    setTasks((prevTasks) =>
      prevTasks.map((task) =>
        task.id === updatedTask.id ? updatedTask : task
      )
    );
  };

  // Handle task deletion
  const handleDeleteTask = async (taskId) => {
    try {
      const response = await deleteTask(taskId); // Call delete function to delete the task
      if (response) {
        setTasks((prevTasks) => prevTasks.filter((task) => task.id !== taskId));
      }
    } catch (error) {
      console.error("Error deleting task:", error);
    }
  };

  return (
    <div className="task-list-container">
      <h1>Real-Time Task Management Dashboard</h1>

      <table className="task-table">
        <thead>
          <tr>
            <th>Task ID</th>
            <th>Task Name</th>
            <th>Status</th>
            <th>Created At</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.length === 0 ? (
            <tr>
              <td colSpan="5">No tasks available</td>
            </tr>
          ) : (
            tasks.map((task) => (
              <tr key={task.id}>
                <td>{task.id}</td>
                <td>{task.name}</td>
                <td>{task.status}</td>
                <td>{task.createdAt}</td>
                <td>
                  <ActionIcons
                    onEditClick={() => handleEditTask(task)}
                    onDeleteClick={() => handleDeleteTask(task.id)}
                  />
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      {/* Edit Task Status Modal */}
      <UpdateTaskStatusModal
        isOpen={isModalOpen}
        onClose={() => setModalOpen(false)}
        task={currentTask}
        onTaskUpdated={handleTaskUpdated}
      />
    </div>
  );
};

export default TaskList;
